const Sidebar = ({ userName, groups }) => {
    const [isCalendarOpen, setIsCalendarOpen] = useState(false);
  
    const toggleCalendar = () => {
      setIsCalendarOpen(!isCalendarOpen);
    };
  
    // 화살표 버튼 스타일
    const arrowButtonStyle = {
      position: 'absolute',
      top: '50%', // 상단에서 50%의 위치에 배치하여 수직 중앙에 오도록 설정
      right: isCalendarOpen ? '0' : '-40px', // 사이드바의 오른쪽 가장자리에 맞춤
      transform: 'translateY(-50%)', // 버튼을 수직 중앙으로 정렬
      cursor: 'pointer',
      background: 'none', // 버튼 배경 제거
      border: 'none', // 버튼 테두리 제거
      color: 'gray', // 버튼 아이콘 색상 설정
      zIndex: 10, // 다른 컨텐츠 위에 버튼이 오도록 z-index 설정
    };
  
    // 달력 스타일
    const calendarStyle = {
      position: 'fixed',
      top: 0, // 상단에서부터 시작
      right: isCalendarOpen ? '0' : '-300px', // 화면 오른쪽에서 시작
      width: '300px', // 달력의 너비 설정
      height: '100%', // 전체 높이
      backgroundColor: '#f0f0f0', // 배경색 설정
      transition: 'right 0.3s', // 부드러운 슬라이드 효과
      zIndex: 5, // 사이드바 아래에 달력이 오도록 z-index 설정
    };
  
    return (
      <div style={{ position: 'relative', width: '250px' }}>
        <div style={{ borderRight: '1px solid #ccc', height: '100vh', position: 'relative' }}>
          <MyInfo userName={userName} />
          <GroupList groups={groups} />
          <button onClick={toggleCalendar} style={arrowButtonStyle}>
            {isCalendarOpen ? '<' : '>'}
          </button>
        </div>
        <div style={calendarStyle}>
          {/* 달력 컴포넌트 또는 내용 */}
        </div>
      </div>
    );
  };
  
  export default Sidebar;
