import React from 'react';

class WelcomeScreen extends React.Component {
  render() {
    const buttonStyle = {
      background: `url('/kakao_login_medium_narrow.png') no-repeat center center`,
      backgroundSize: 'cover', // 이미지가 버튼 크기에 맞게 조절되도록 함
      width: '250px', // 버튼의 너비, 이미지에 맞게 조절 가능
      height: '50px', // 버튼의 높이, 이미지에 맞게 조절 가능
      border: 'none', // 테두리 없음
      cursor: 'pointer' // 마우스 오버 시 커서 변경
    };

    return (
      <div style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', backgroundColor: '#ffffff' }}>
        <h1>안녕하세요 OOO입니다.</h1>
        <p>OO을 이용해 모임을 관리해보세요!</p>
        {/* 이미지를 배경으로 하는 버튼 */}
        <button style={buttonStyle} onClick={this.handleLoginClick}>
          {/* 버튼 위의 텍스트가 필요하지 않으면 이 부분을 비워둘 수 있습니다. */}
        </button>
      </div>
    );
  }

  handleLoginClick = () => {
    // 로그인 처리를 위한 핸들러 함수
    console.log('로그인 버튼이 클릭되었습니다.');
    // 여기에 로그인 로직을 구현하거나, 다른 페이지로 리다이렉트 처리를 할 수 있습니다.
  }
}

export default WelcomeScreen;
