import React, { useState } from 'react';
import MyInfo from './MyInfo';
import GroupList from './GroupList';

const Sidebar = ({ userName, groups }) => {
    const [isCalendarOpen, setIscalendarOpen] = useState(false);

    const toggleCalendar = () => {
        setIscalendarOpen(!isCalendarOpen);
    };

    const arrowButtonStyle = {
        position: 'absolute',
        top: '50%',
        left: isCalendarOpen ? '0' : '210px',
        transform: 'translateY(-50%)',
        cursor: 'pointer',
        padding: '10px',
        fontSize: '24px',
        background: 'none',
        border: 'none',
        color: 'gray',
        zIndex: 1000,
    };

    const expandedStyle = {
        position: 'fixed',
        top: 0,
        left: isCalendarOpen ? '0' : '250px',
        width: '100vw',
        height: '100%',
        backgroundColor: '#f0f0f0',
        translation: 'right 0.3s ease-in-out',
        padding: '20px',
        boxSizing: 'border-box',
        zIndex: 500,
    };

    return (
        <div style={{ position: 'relative'}}>
            <div style={{ width: '250px', borderRight: '1px solic #ccc', height: '100%'}}>
                <MyInfo userName={userName} />
                <GroupList groups={groups} />
            </div>
            <button onClick={toggleCalendar} style={{arrowButtonStyle}}>
                {isCalendarOpen ? '<' : '>'}
            </button>
            <div style={{expandedStyle}}>
            </div>
        </div>
    );
};

export default Sidebar;
