import React, { useState } from 'react';
import MyInfo from './MyInfo';
import GroupList from './GroupList';

const Sidebar = ({ userName, groups }) => {
  const [isCalendarOpen, setIsCalendarOpen] = useState(false);

  const toggleCalendar = () => {
    setIsCalendarOpen(!isCalendarOpen);
};

return (
  <div style={{ display: 'flex', position: 'relative' }}>
    <div style={{ width: '250px', borderRight: '1px solid #ccc' }}>
      <MyInfo userName={userName} />
      <GroupList groups={groups} />
    </div>
    <button onClick={toggleCalendar} style={{
      position: 'absolute',
      top: 0,
      right: isCalendarOpen ? '-20px' : '-40px', // 버튼 위치 조정
      transform: isCalendarOpen ? 'rotate(180deg)' : 'none', // 달력이 열렸을 때 버튼 방향 변경
      transition: 'right 0.3s, transform 0.3s',
      cursor: 'pointer',
      padding: '10px',
      border: 'none',
      background: 'gray',
      color: 'white'
    }}>
      {isCalendarOpen ? '<' : '>'}
    </button>
    {isCalendarOpen && (
      <div style={{
        position: 'absolute',
        top: 0,
        right: '0',
        width: '300px', // 달력 창의 너
        height: '100%', // 달력 창의 높이를 조절합니다.
        backgroundColor: '#f0f0f0', // 달력 창의 배경색을 설정합니다.
        boxShadow: '-2px 0 5px rgba(0,0,0,0.5)', // 달력 창에 그림자 효과를 추가합니다.
        transition: 'right 0.3s', // 슬라이드 효과를 부드럽게 만듭니다.
      }}>
        {/* 달력 컴포넌트나 달력 내용이 여기 들어갑니다. */}
        달력 내용
      </div>
    )}
  </div>
);
};

export default Sidebar;
