import React, { useState } from 'react';
import MyInfo from './MyInfo';
import GroupList from './GroupList';

const Sidebar = ({ userName, groups }) => {
    const [isCalendarOpen, setIscalendarOpen] = useState(false);

    const toggleCalendar = () => {
        setIscalendarOpen(!isCalendarOpen);
    };

    return (
        <div style={{ display: 'flex', position: 'relative', height: '100vh'}}>
            <div style={{ width: '250px', borderRight: '1px solic #ccc', height: '100%'}}>
                <MyInfo userName={userName} />
                <GroupList groups={groups} />
            </div>
            <button onClick={toggleCalendar} style={{
                position: 'absolute',
                top: '50%',
                right: isCalendarOpen ? '0px': '-40px',
                transform: 'translateY(-50%)',
                cursor: 'pointer',
                padding: '10px',
                border: 'none',
                background: 'gray',
                color: 'white',
                zIndex: 1000,
            }}>
                {isCalendarOpen ? '<' : '>'}
            </button>
            <div style={{
                position: 'absolute',
                top: 0,
                right: isCalendarOpen ? '0': '-100%',
                width: '100%',
                height: '100%',
                backgroundColor: '#f0f0f0',
                translation: 'right 0.5s ease-in-out',
                zIndex: 500,
                padding: '20px',
            }}>
            </div>
        </div>
    );
};

export default Sidebar;
