import React, { useState } from 'react';
import MyInfo from './MyInfo';
import GroupList from './GroupList';

const Sidebar = ({ userName, groups }) => {
  const [isCalendarOpen, setIsCalendarOpen] = useState(false);

  // 토글 함수
  const toggleCalendar = () => {
    setIsCalendarOpen(!isCalendarOpen);
  };

  // 화살표 버튼 스타일
  const arrowButtonStyle = {
    position: 'absolute',
    top: '50%',
    left: '100%', // 사이드바 오른쪽 끝
    transform: 'translateY(-50%)', // 버튼을 수직 중앙에 위치
    transform: isCalendarOpen ? 'translateY(-50%) rotate(180deg)' : 'translateY(-50%)', // 화살표 방향
    cursor: 'pointer',
    background: '#fff', // 버튼 배경색
    border: 'none', // 버튼 테두리 제거
    color: 'gray', // 버튼 색상
    zIndex: 10, // 버튼을 다른 컨텐츠 위에 놓음
  };

  // 달력 스타일
  const calendarStyle = {
    position: 'absolute',
    top: 0,
    left: isCalendarOpen ? '0' : '-250px', // 달력 위치
    width: '250px', // 달력 너비
    height: '100%', // 달력 높이
    backgroundColor: '#f0f0f0', // 달력 배경색
    transition: 'left 0.5s ease-in-out', // 부드러운 애니메이션 효과
    zIndex: 5, // 달력 z-index
  };

  return (
    <div style={{ position: 'relative', width: '250px' }}>
      <div style={{ borderRight: '1px solid #ccc', height: '100vh' }}>
        <MyInfo userName={userName} />
        <GroupList groups={groups} />
      </div>
      <div style={calendarStyle}>
        {/* 달력 컴포넌트 또는 내용 */}
      </div>
      <button onClick={toggleCalendar} style={arrowButtonStyle}>
        {isCalendarOpen ? '◀' : '▶'} {/* 화살표 아이콘 방향 */}
      </button>
    </div>
  );
};

export default Sidebar;
