import React, { useState } from 'react';
import MyInfo from './MyInfo';
import GroupList from './GroupList';

const Sidebar = ({ userName, groups }) => {
  const [isCalendarOpen, setIsCalendarOpen] = useState(false);

  const toggleCalendar = () => {
    setIsCalendarOpen(!isCalendarOpen);
  };

  const sidebarStyle = {
    width: '250px',
    height: '100vh', // 전체 뷰포트 높이를 사용
    borderRight: '1px solid #ccc',
    position: 'relative', // 상대적 위치 설정
    backgroundColor: '#fff', // 사이드바 배경색
    zIndex: 5 // 사이드바가 콘텐츠 위에 나타나도록 z-index 설정
  };

  const arrowButtonStyle = {
    position: 'absolute',
    top: '50%', // 상단에서 50%의 위치에 배치하여 수직 중앙에 오도록 설정
    left: '100%', // 사이드바의 완전 오른쪽에 버튼을 배치
    transform: 'translateY(-50%) rotate(180deg)', // 버튼을 수직 중앙으로 정렬하고 화살표 방향 전환
    cursor: 'pointer',
    border: 'none', // 버튼 테두리 제거
    background: '#fff', // 버튼 배경색
    zIndex: 10 // 화살표 버튼이 사이드바 위에 오도록 설정
  };

  const calendarStyle = {
    position: 'absolute',
    top: 0,
    left: isCalendarOpen ? '250px' : '0', // 사이드바 바로 오른쪽에서 시작
    width: '250px', // 달력의 너비
    height: '100%', // 달력의 높이
    backgroundColor: '#f0f0f0',
    transition: 'left 0.5s ease-in-out', // 부드러운 애니메이션
    zIndex: 5 // 달력이 사이드바 아래에 나타나도록 z-index 설정
  };

  return (
    <div style={{ position: 'relative' }}>
      <div style={sidebarStyle}>
        <MyInfo userName={userName} />
        <GroupList groups={groups} />
      </div>
      <div style={calendarStyle}>
        {/* 달력 컴포넌트 또는 내용 */}
      </div>
      <button onClick={toggleCalendar} style={arrowButtonStyle}>
        {isCalendarOpen ? '◀' : '▶'} {/* 화살표 아이콘 방향 조정 */}
      </button>
    </div>
  );
};

export default Sidebar;
