import React, { useState } from 'react';
import MyInfo from './MyInfo';
import GroupList from './GroupList';

const Sidebar = ({ userName, groups }) => {
  const [isCalendarOpen, setIsCalendarOpen] = useState(false);

  const toggleCalendar = () => {
    setIsCalendarOpen(!isCalendarOpen);
  };

  const sidebarStyle = {
    width: '250px',
    height: '100vh', // 전체 뷰포트 높이를 사용
    borderRight: '1px solid #ccc'
  };

  const arrowButtonStyle = {
    position: 'absolute',
    top: '50%', // 상단에서 50%의 위치에 배치하여 수직 중앙에 오도록 설정
    right: isCalendarOpen ? '250px' : '0', // 화살표가 달력 오른쪽 끝에 붙도록 설정
    transform: 'translateY(-50%)', // 버튼을 수직 중앙으로 정렬
    zIndex: 20 // 화살표 버튼이 달력 위에 오도록 설정
  };

  const calendarStyle = {
    position: 'fixed',
    top: 0,
    right: isCalendarOpen ? '0' : '-250px', // 달력이 오른쪽으로 펼쳐지도록 설정
    width: '250px', // 달력의 너비
    height: '100%', // 달력의 높이
    backgroundColor: '#f0f0f0',
    transition: 'right 0.5s ease-in-out', // 부드러운 애니메이션
    zIndex: 10 // 달력이 컨텐츠 영역 위에 오도록 설정
  };

  return (
    <div style={{ position: 'relative' }}>
      <div style={sidebarStyle}>
        <MyInfo userName={userName} />
        <GroupList groups={groups} />
      </div>
      <div style={calendarStyle}>
        {/* 달력 컴포넌트 또는 내용 */}
      </div>
      <button onClick={toggleCalendar} style={arrowButtonStyle}>
        {isCalendarOpen ? '▶' : '◀'} {/* 화살표 아이콘 방향 조정 */}
      </button>
    </div>
  );
};

export default Sidebar;
