import React, { useState } from 'react';
import MyInfo from './MyInfo';
import GroupList from './GroupList';

const Sidebar = ({ userName, groups }) => {
    const [isCalendarOpen, setIsCalendarOpen] = useState(false);
  
    const toggleCalendar = () => {
      setIsCalendarOpen(!isCalendarOpen);
    };

    const sidebarStyle = {
        width: '250px',
        height: '100vh',
        borderRight: '1px solid #ccc',
        position: 'relative', // 상대적 위치 설정
    };


    const arrowButtonStyle = {
        position: 'absolute',
        top: '50%',
        right: '-30px', // 사이드바 바깥으로 위치 조정
        transform: 'translateY(-50%)',
        cursor: 'pointer',
        border: 'none',
        background: '#fff',
        zIndex: 30, // 다른 컨텐츠 위로
        transition: 'transform 0.3s ease-in-out',
    };

    const calendarStyle = {
        position: 'absolute',
        top: 0,
        left: isCalendarOpen ? '250px' : '250px', // 사이드바의 오른쪽 부분부터 시작
        transform: isCalendarOpen ? 'translateX(0)' : 'translateX(-100%)', // 보이지 않을 때 왼쪽으로 숨김
        width: '250px',
        height: '100%',
        backgroundColor: '#f0f0f0',
        boxShadow: '0px 0 10px rgba(0,0,0,0.2)',
        transition: 'transform 0.3s ease-in-out',
        visibility: isCalendarOpen ? 'visible' : 'hidden', // 토글에 따라 visibility 조정
        opacity: isCalendarOpen ? 1 : 0, // 토글에 따라 opacity 조정
        zIndex: 5,
    };

    return (
        <div style={{ position: 'relative', width: '250px', height: '100vh' }}>
            <div style={sidebarStyle}>
                <MyInfo userName={userName} />
                <GroupList groups={groups} />
            </div>
            <div style={calendarStyle}>
                {/* 달력 컴포넌트 또는 내용 */}
            </div>
            <button onClick={toggleCalendar} style={arrowButtonStyle}>
                {isCalendarOpen ? '◀' : '▶'}
            </button>
        </div>
    );
};

export default Sidebar;
