import React from 'react';

class WelcomeScreen extends React.Component {
  render() {
    const buttonStyle = {
      background: `url('/kakao_login_medium_narrow.png') no-repeat center center`,
      backgroundSize: 'cover',
      width: '250px', 
      height: '50px', 
      border: 'none',
      cursor: 'pointer'
    };

    const textStyle = {
      paddingTop: '100px',
      paddingBottom: '100px'
    }

    return (
      <div style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', backgroundColor: '#ffffff' }}>
        <h1 style={textStyle}>안녕하세요, OOO입니다.</h1>
        <h2 style = {textStyle}>OO을 이용해 모임을 관리해보세요!</h2>
        <button style={buttonStyle} onClick={this.handleLoginClick}>
        </button>
      </div>
    );
  }

  handleLoginClick = () => {
    // 로그인 처리를 위한 핸들러 함수
    console.log('로그인 버튼이 클릭되었습니다.');
    // 로그인 로직 필요
  }
}

export default WelcomeScreen;
